import Fetch from 'isomorphic-unfetch';
import Layout from '../components/Layout';

import Prices from '../components/Prices';
import stocks from "../models/stockArray";
import stockapi from "../models/apiPull"

const Index = (props) => (
  <Layout>
    <div>
      <h1>Welcome to BitzPrice</h1>
      <p>Check current Stock Price, first test Aug 25</p>

      {console.log( props.URLNamData )}
      <Prices stockapi = { props.URLNameData, props.URLStockPrice, props.URLMarketCapitalization, props.debtRatio} />

    </div>
  </Layout>
);

Index.getInitialProps = async function() {
    for (var i = 0; i < stocks.length; i++) {
  const URLName = await Fetch("https://eodhistoricaldata.com/api/fundamentals/" + stocks[i] + ".US?api_token=" + process.env.EOD_KEY + "&filter=General::Name");
  const URLNameData = await URLName.json();
  const URLStockPrice = "https://eodhistoricaldata.com/api/real-time/" + stocks[i] + ".US?api_token=" + process.env.EOD_KEY + "&fmt=json&filter=close";
  const URLStockPriceData = await URLStockPrice.json();
  const URLMarketCapitalization = "https://eodhistoricaldata.com/api/fundamentals/" + stocks[i] + ".US?api_token=" + process.env.EOD_KEY + "&filter=Highlights::MarketCapitalization";
  const URLMarketCapitalizationData = await URLMarketCapitalization .json();
  const URLDebt = "https://eodhistoricaldata.com/api/fundamentals/" + stocks[i] + ".US?api_token=" + process.env.EOD_KEY + "&filter=Financials::Balance_Sheet::quarterly";
  const URLDebtData = await URLDebt.json();
  const URLEBITDA= "https://eodhistoricaldata.com/api/fundamentals/" + stocks[i] + ".US?api_token=" + process.env.EOD_KEY + "&filter=Highlights::EBITDA";
  const URLEBITDData = await URLEBITDA.json();
  const debtRatio = URLDebt/URLEBITDA



  return {
    URLNameData: URLNameData,
    URLStockPriceData: URLStockPriceData,
    URLMarketCapitalization: URLMarketCapitalization,
    debtRatio: debtRatio



  };
}

}

export default Index;
